mapper.select:

select *
from (
         select @rownum:=@rownum+1 as rn, car.*
         from(select @rownum :=0)as rn, car
     ) car
where rn > (#{pageNum} -1)* #{amount} and rn <= #{pageNum} * #{amount};

mapper.update:
UPDATE Car
SET isDel= 'Y'
WHERE carId = #{carId}

UPDATE Car
SET readCount = readCount + 1
WHERE carId = #{carId}

update Car
SET thumb = #{thumb}
WHERE carId = #{carId}
